pub fn fontUniformBlockSize(ub_name: []const u8) ?usize {
    if (std.mem.eql(u8, ub_name, "vs_params")) {
        return @sizeOf(VsParams);
    }
    return null;
}
pub fn fontUniformOffset(ub_name: []const u8, u_name: []const u8) ?usize {
    if (std.mem.eql(u8, ub_name, "vs_params")) {
        if (std.mem.eql(u8, u_name, "viewMatrix")) {
            return 0;
        }
        if (std.mem.eql(u8, u_name, "textureVec")) {
            return 64;
        }
    }
    return null;
}
pub fn fontUniformDesc(ub_name: []const u8, u_name: []const u8) ?sg.GlslShaderUniform {
    if (std.mem.eql(u8, ub_name, "vs_params")) {
        if (std.mem.eql(u8, u_name, "viewMatrix")) {
            var desc: sg.GlslShaderUniform = .{};
            desc.type = .MAT4;
            desc.array_count = 0;
            desc.glsl_name = "viewMatrix";
            return desc;
        }
        if (std.mem.eql(u8, u_name, "textureVec")) {
            var desc: sg.GlslShaderUniform = .{};
            desc.type = .FLOAT4;
            desc.array_count = 0;
            desc.glsl_name = "textureVec";
            return desc;
        }
    }
    return null;
}
