        return 0;
    }
    if (std.mem.eql(u8, attr_name, "vertex_rotation")) {
        return 1;
    }
    if (std.mem.eql(u8, attr_name, "vertex_size")) {
        return 2;
    }
    if (std.mem.eql(u8, attr_name, "vertex_pivot")) {
        return 3;
    }
    if (std.mem.eql(u8, attr_name, "vertex_texture")) {
        return 4;
    }
    if (std.mem.eql(u8, attr_name, "vertex_color")) {
        return 5;
    }
    return null;
}
